mapper.update:
update t_todo set title=#{title},
					writer=#{writer},
					finished=#{finished},
					dueDate=#{dueDate}
						where tno=#{tno}

mapper.select:
select tno, title, dueDate, writer, finished, regdate
from (
select
/*+ index_desc(t_todo SYS_C007025)*/
rownum rn, tno, title, dueDate, writer, finished, regdate
from t_todo


where rownum<= #{pageNum}*#{amount}
	)
where rn > (#{pageNum}-1)*#{amount}
